import axios from 'axios'
import React, { useEffect, useState } from 'react'
import { Link, useParams } from 'react-router-dom'
import Loading from '../Loading/Loading';
// PlatFormBrowser


export default function SortBy() {
    const [sortData,setSortData]=useState([]);
    const [visible,setVisible]=useState(20);
    const[param,setParams]=useState(null);
    const  [loading,setLoading]=useState(true);
    let params=useParams();
  
    const showMoreItem =()=>{
      setVisible((prevValue) => prevValue + 20);
    }
     let url='https://free-to-play-games-database.p.rapidapi.com/api/games';
  async function getData(){
    let {data}= await axios.get(url,{
      params: {'sort-by':params.sort},
    headers: {
          'X-RapidAPI-Key': '523aa4b4cfmsh179cf1aa8840e88p1d19cdjsne54d3dde7858',
          'X-RapidAPI-Host': 'free-to-play-games-database.p.rapidapi.com'
        }})
        console.log(data);
        setSortData(data);
        setLoading(false);
  }
  useEffect(()=>{
    setParams(params);
    getData();
  },[params])
  
  
    return (
      <>
        {loading && <Loading/>}
        {!loading && <>   <section>
        <div classNameName="container mt-5">
          <div classNameName="row g-4">
            {sortData.slice(0,visible).map((sortitem,index)=> <div classNameName="col-md-3" key={index}>
              <Link to={'/gameDetails/'+sortitem.id}>
              <div classNameName="sortitem  pb-3 shadow mb-5 cursolPointer scaleItem">
             <img src={sortitem.thumbnail} classNameName='w-100 mb-3' alt="" />
               <div classNameName='position-relative px-2'>
               <h4 classNameName='px-2'>{sortitem.title.slice(0,15)}</h4>
                <span classNameName="badge text-bg-primary px-3 mx-3 py-2 position-absolute end-0 bottom-0">Free</span>
               </div>
               <div classNameName='px-2'>
               <p classNameName='px-2'>{sortitem.short_description.slice(0,25)+'....'}</p>
               </div>
               <div classNameName='d-flex px-2 justify-content-between'>
               <div ><i classNameName="fas fa-plus-square px-2 text-muted"></i></div>
               <div><span classNameName='LightColor rounded px-1 text-dark'>{sortitem.genre}</span> {sortitem.platform=="PC (Windows)"?<i classNameName="fa-brands fa-windows text-muted"></i>:<i classNameName="text-muted fa-solid fa-window-maximize"></i>}</div>
               </div>
              </div>
              </Link>
            </div> )}
           
          </div>
          <div classNameName='text-center'>
          <button classNameName='btn btn-outline-secondary mb-4' onClick={showMoreItem}>More Games <i classNameName="fa-solid fa-chevron-right text-muted"></i></button>
          </div>
        </div>
      </section> </>}
      </>
    )
  }
